import React from "react";
import { Route, Switch } from "react-router-dom";
// javascript plugin used to create scrollbars on windows
import PerfectScrollbar from "perfect-scrollbar";
import logo from "assets/img/react-logo.png";
import {getCurrentUser} from "../../util/APIUtils";

// core components
import AdminNavbar from "components/Navbars/AdminNavbar.jsx";
import Footer from "components/Footer/Footer.jsx";
import Sidebar from "components/Sidebar/Sidebar.jsx";
import FixedPlugin from "components/FixedPlugin/FixedPlugin.jsx";
import dashboardRoutesClub from "routes/dashboardAdmin.jsx";
import dashboardRoutesUser from "routes/dashboardUser.jsx";

import Transfer from "Sections/Transfer.jsx"

import NotFound from "../NotFound";

var ps;

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      currentUser: this.props.currentUser,
      isLoading: true,
      backgroundColor: "primary",
      sidebarOpened:
        document.documentElement.className.indexOf("nav-open") !== -1
    };
    this.loadUserProfile = this.loadUserProfile.bind(this);
  }
  loadUserProfile() {
    this.setState({
      isLoading: true
    });

    getCurrentUser()
        .then(response => {
          this.setState({
            user: response,
            isLoading: false
          });
        }).catch(error => {
      if(error.status === 404) {
        this.setState({
          notFound: true,
          isLoading: false
        });
      } else {
        this.setState({
          serverError: true,
          isLoading: false
        });
      }
    });
  }
  componentDidMount() {
    if (navigator.platform.indexOf("Win") > -1) {
      document.documentElement.className += " perfect-scrollbar-on";
      document.documentElement.classList.remove("perfect-scrollbar-off");
      ps = new PerfectScrollbar(this.refs.mainPanel, { suppressScrollX: true });
      let tables = document.querySelectorAll(".table-responsive");
      for (let i = 0; i < tables.length; i++) {
        ps = new PerfectScrollbar(tables[i]);
      }
    }
    this.loadUserProfile();
  }
  componentWillUnmount() {
    if (navigator.platform.indexOf("Win") > -1) {
      ps.destroy();
      document.documentElement.className += " perfect-scrollbar-off";
      document.documentElement.classList.remove("perfect-scrollbar-on");
    }
  }
  componentDidUpdate(e) {
    if (e.history.action === "PUSH") {
      if (navigator.platform.indexOf("Win") > -1) {
        let tables = document.querySelectorAll(".table-responsive");
        for (let i = 0; i < tables.length; i++) {
          ps = new PerfectScrollbar(tables[i]);
        }
      }
      document.documentElement.scrollTop = 0;
      document.scrollingElement.scrollTop = 0;
      this.refs.mainPanel.scrollTop = 0;
    }
    //this.loadUserProfile();
  }
  // this function opens and closes the sidebar on small devices
  handleToggleSidebar = () => {
    document.documentElement.classList.toggle("nav-open");
    this.setState({ sidebarOpened: !this.state.sidebarOpened });
  };
  /*getRoutes = routes => {
    return routes.map((prop, key) => {
      if (prop.layout === "/admin") {
        return (
          <Route
            path={prop.layout + prop.path}
            component={prop.component}
            key={key}
          />
        );
      } else {
        return null;
      }
    });
  };*/
  handleBgClick = color => {
    this.setState({ backgroundColor: color });
  };
  getBrandText = path => {
    for (let i = 0; i < routes.length; i++) {
      if (
        this.props.location.pathname.indexOf(
          routes[i].layout + routes[i].path
        ) !== -1
      ) {
        return routes[i].name;
      }
    }
    return "Brand";
  };

  render() {
    let dashboardRoutes;
    {this.props.currentUser.role==="ROLE_CLUB"?(dashboardRoutes=dashboardRoutesClub):(dashboardRoutes=dashboardRoutesUser);}
    const switchRoutes = this.props.currentUser.role==="ROLE_CLUB"?(
        <Switch>
          <Route exact path="/dash/transfer"
                 render={(props) => <Transfer currentUser={this.state.currentUser} {...props} />}>
          </Route>
          <Route component={NotFound} />
        </Switch>
    ):(
        <Switch>
            {/*<Route exact path="/dash/dashboard"
                 render={(props) => <DashboardPage currentUser={this.state.currentUser} {...props} />}>
          </Route>
          <Route exact path="/dash/profile"
                 render={(props) => <Profile currentUser={this.state.currentUser} {...props} />}>
          </Route>*/}
          <Route component={NotFound} />
        </Switch>
    );
    return (
      <>
        <div className="wrapper">
          <Sidebar
            {...this.props}
            routes={dashboardRoutes}
            bgColor={this.state.backgroundColor}
            logo={{
              outterLink: "https://www.bcp.com/",
              text: "Banque Populaire",
              imgSrc: logo
            }}
            toggleSidebar={this.handleToggleSidebar}
          />
          <div
            className="main-panel"
            ref="mainPanel"
            data={this.state.backgroundColor}
          >
            <AdminNavbar
              {...this.props}
              /*routes={dashboardRoutes}*/
              brandText={this.getBrandText(this.props.location.pathname)}
              toggleSidebar={this.toggleSidebar}
              sidebarOpened={this.state.sidebarOpened}
              handleLogout={this.props.handleLogout}
            />
            {/*<Switch>{this.getRoutes(routes)}</Switch>*/}
            {/*this.getRoutes*/(switchRoutes)}
            {// we don't want the Footer to be rendered on map page
            this.props.location.pathname.indexOf("maps") !== -1 ? null : (
              <Footer fluid />
            )}
          </div>
        </div>
        {/*<FixedPlugin
          bgColor={this.state.backgroundColor}
          handleBgClick={this.handleBgClick}
        />*/}
      </>
    );
  }
}

export default Dashboard;
