import React, { Component } from 'react';

import {
  Route,
  withRouter,
  Switch,
  Redirect
} from 'react-router-dom';

import { ACCESS_TOKEN } from '../variables/constants';

import Dashboard from '../views/User/Dashboard/Dashboard.jsx';
import Login from '../views/Login';
import NotFound from '../views/NotFound';
import PrivateRoute from './PrivateRoute';

// Sweet alert
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";
const MySwal = withReactContent(Swal);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUser: null,
      isAuthenticated: true,//false,
      isLoading: true
    };
    this.handleLogout = this.handleLogout.bind(this);
  }

  handleLogout = ()=> {
    localStorage.removeItem(ACCESS_TOKEN);

    this.setState({
      currentUser: null,
      isAuthenticated: false
    });
    const Toast = MySwal.mixin({
      toast: true,
      position: 'top-end',
      showConfirmButton: false,
      timer: 2000
    });
    Toast.fire({
      type: 'success',
      title: 'Vous êtes déconnecté avec succès.'
    });
    this.props.history.push('/login');
  };

  render() {
    /*if(this.state.isLoading) {
      return <LoadingIndicator />
    }*/
    return (
        <>
            <Switch>
              <Route path="/login"
                     render={(props) => <Login {...props} />} />
              <PrivateRoute path="/user" component={Dashboard} handleLogout={this.handleLogout} history={this.props.history}/>
              <Redirect exact from="/" to="/login" />
              <Route component={NotFound} />
            </Switch>
        </>
    );
  }
}

export default withRouter(App);
