import React from "react";
// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";
import InputLabel from "@material-ui/core/InputLabel";
// core components
import GridItem from "components/Grid/GridItem.jsx";
import GridContainer from "components/Grid/GridContainer.jsx";
import CustomInput from "components/CustomInput/CustomInput.jsx";
import Button from "components/CustomButtons/Button.jsx";
import Card from "components/Card/Card.jsx";
import CardAvatar from "components/Card/CardAvatar.jsx";
import CardBody from "components/Card/CardBody.jsx";
import CardFooter from "components/Card/CardFooter.jsx";

import FormControl from "@material-ui/core/FormControl";
import Datetime from "react-datetime";
import InputAdornment from "@material-ui/core/InputAdornment";
import {createEvent, signup, updateUserProfile, uploadSingleFile} from "../../../util/APIUtils";
import NotFound from '../../NotFound/NotFound';
import ServerError from '../../ServerError/ServerError';
import LoadingIndicator  from '../../../components/LoadingIndicator/LoadingIndicator';

import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";
import IconButton from "@material-ui/core/IconButton";
import Close from "@material-ui/icons/Close";
import DialogContent from "@material-ui/core/DialogContent";
import DialogActions from "@material-ui/core/DialogActions";
import Slide from "@material-ui/core/Slide";
import {notification} from "antd/lib/index";

const styles = {
  cardCategoryWhite: {
    color: "rgba(255,255,255,.62)",
    margin: "0",
    fontSize: "14px",
    marginTop: "0",
    marginBottom: "0"
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none"
  },
  labelLeft: {
    marginTop: "25px",
    textAlign: "left",
    color: "#AAAAAA"
  }
};

function Transition(props) {
    return <Slide direction="down" {...props} />;
}

class Profile extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            name: props.currentUser.name,
            avatar: props.currentUser.avatar,
            username: props.currentUser.username,
            email: props.currentUser.email,
            category: props.currentUser.category,
            description: props.currentUser.description,
            role: props.currentUser.role,

            isLoading: false,
            classicModal: false,
            file: null
        };
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleFileChange = this.handleFileChange.bind(this);
        this.handleFileSubmit = this.handleFileSubmit.bind(this);
    }

    componentDidMount() {
        this.setState({
            name: this.props.currentUser.name,
            avatar: this.props.currentUser.avatar,
            username: this.props.currentUser.username,
            email: this.props.currentUser.email,
            category: this.props.currentUser.category,
            description: this.props.currentUser.description,
            role: this.props.currentUser.role,

            classicModal: false,
            file: null
        });
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleFileChange = this.handleFileChange.bind(this);
        this.handleFileSubmit = this.handleFileSubmit.bind(this);
    }

    handleSubmit(event) {
        event.preventDefault();

        const userRequest = {
            name: this.state.name,
            description: this.state.description,
            category: this.state.category,
        };

        updateUserProfile(this.props.currentUser.username, userRequest)
            .then(response => {
                notification.success({
                    message: 'Event App',
                    description: "Modification effectuée avec succès",
                });
                //this.props.history.push("/login");
            }).catch(error => {
            notification.error({
                message: 'Event App',
                description: error.message || 'Pardon! Quelque chose s\'est mal passé. Veuillez réessayer!'
            });
        });
    }


    handleClickOpen(modal) {
        let x = [];
        x[modal] = true;
        this.setState(x);
    }
    handleClose(modal) {
        let x = [];
        x[modal] = false;
        this.setState(x);
    }
    handleFileSubmit(event) {
        event.preventDefault();
        const file = this.state.file;
        //alert(file);
        if(file === null) {
            notification.error({
                message: 'Event App',
                description: 'Please select a file!'
            });
        }
        else
            uploadSingleFile(file)
                .then(response => {
                    this.handleClose("classicModal");
                    notification.success({
                        message: 'Event App',
                        description: "Votre photo a été mise à jour avec succès.",
                    });
                }).catch(error => {
                notification.error({
                    message: 'Event App',
                    description: error.message || 'Pardon! Quelque chose s\'est mal passé. Veuillez réessayer!'
                });
        });
    }
    handleFileChange(event) {
        const file = event.target.files[0];
        this.setState({
            file: file
            //...this.validateDescription(value)
        });
    }

    handleInputChange(event) {
        const target = event.target;
        const inputName = target.id;
        const inputValue = target.value;

        this.setState({
            [inputName] : inputValue
        });
    }


    render() {
        if(this.state.isLoading) {
            return <LoadingIndicator />;
        }

        if(this.state.notFound) {
            return <NotFound />;
        }

        if(this.state.serverError) {
            return <ServerError />;
        }

  const { classes } = this.props;
  return (
    <div>
      <GridContainer>
        <GridItem xs={12} sm={12} md={12}>
          <Card profile>
            <CardAvatar profile>
              <a onClick={() => this.handleClickOpen("classicModal")}>
                  <img src={"data:image/jpeg;base64," + this.state.avatar} alt="..."/>
              </a>
                <Dialog
                    classes={{
                        root: classes.center,
                        paper: classes.modal
                    }}
                    open={this.state.classicModal}
                    TransitionComponent={Transition}
                    keepMounted
                    onClose={() => this.handleClose("classicModal")}
                    aria-labelledby="classic-modal-slide-title"
                    aria-describedby="classic-modal-slide-description"
                >
                    <DialogTitle
                        id="classic-modal-slide-title"
                        disableTypography
                        className={classes.modalHeader}
                    >
                        <IconButton
                            className={classes.modalCloseButton}
                            key="close"
                            aria-label="Close"
                            color="inherit"
                            onClick={() => this.handleClose("classicModal")}
                        >
                            <Close className={classes.modalClose} />
                        </IconButton>
                        <h4 className={classes.modalTitle}>Modifier la photo</h4>
                    </DialogTitle>
                    <DialogContent
                        id="classic-modal-slide-description"
                        className={classes.modalBody}
                    >
                        <CustomInput
                            labelText="avatar"
                            id="avatar"
                            formControlProps={{
                                fullWidth: true,
                                required: true
                            }}
                            inputProps={{
                                type: "file",
                                onChange: this.handleFileChange,
                            }}
                        />
                    </DialogContent>
                    <DialogActions className={classes.modalFooter}>
                        <Button onClick={this.handleFileSubmit} color="transparent" simple>
                            Modifier
                        </Button>
                        <Button
                            onClick={() => this.handleClose("classicModal")}
                            color="danger"
                            simple
                        >
                            Annuler
                        </Button>
                    </DialogActions>
                </Dialog>
            </CardAvatar>
            <CardBody profile>
              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  <h4 className={classes.cardTitle}>{this.state.name}</h4>
                    <h5 className={classes.cardTitle}>@{this.state.username}</h5>
                </GridItem>
              </GridContainer>
              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Adresse e-mail"
                    id="email"
                    formControlProps={{
                      fullWidth: true,
                        required: true
                    }}
                    inputProps={{
                      disabled: true,
                      value: this.state.email,
                      onChange: (event) => this.handleInputChange(event),
                    }}
                  />
                </GridItem>
              </GridContainer>
              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  <CustomInput
                    labelText="Nom"
                    id="name"
                    formControlProps={{
                      fullWidth: true,
                        required: true
                    }}
                    inputProps={{
                       value: this.state.name,
                       onChange: (event) => this.handleInputChange(event),
                       endAdornment: (
                         <InputAdornment position="start">
                            {this.state.role === "ROLE_CLUB"?(<i className="fas fa-users" />):(<i className="fas fa-user" />)}
                         </InputAdornment>
                       )
                    }}
                  />
                </GridItem>
              </GridContainer>
                {this.state.role === "ROLE_CLUB"?(
                    <GridContainer>
                        <GridItem xs={12} sm={12} md={12}>
                            <CustomInput
                                labelText="Categorie"
                                id="category"
                                formControlProps={{
                                    fullWidth: true,
                                    required: true
                                }}
                                inputProps={{
                                    value: this.state.category,
                                    onChange: (event) => this.handleInputChange(event),
                                }}
                            />
                        </GridItem>
                    </GridContainer>
                ):null
                }
              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  <GridContainer>
                    <GridItem xs={12} sm={12} md={6} className={classes.labelLeft}>
                      <InputLabel className={classes.label}>
                        Date de création
                      </InputLabel>
                      <br />
                      <FormControl fullWidth>
                        <Datetime
                          inputProps={{ placeholder: "Date de création" }}
                        />
                      </FormControl>
                    </GridItem>
                  </GridContainer>
                </GridItem>
              </GridContainer>
                {this.state.role === "ROLE_CLUB"?(
                    <GridContainer>
                        <GridItem xs={12} sm={12} md={12} className={classes.labelLeft}>
                            <InputLabel>Description</InputLabel>
                            <CustomInput
                                labelText="description..."
                                id="description"
                                formControlProps={{
                                    fullWidth: true,
                                    required: true
                                }}
                                inputProps={{
                                    value: this.state.description,
                                    onChange: (event) => this.handleInputChange(event),
                                    multiline: true,
                                    rows: 5
                                }}
                            />
                        </GridItem>
                    </GridContainer>
                ):null
                }
            </CardBody>
            <CardFooter>
              <Button onClick={this.handleSubmit} color="primary">Enregistrer</Button>
              <Button color="primary" simple>Annuler</Button>
            </CardFooter>
          </Card>
        </GridItem>
      </GridContainer>
    </div>
  );
  }
}

export default withStyles(styles)(Profile);
