import React, { Component } from 'react';
import {
  Route,
  withRouter,
  Switch
} from 'react-router-dom';

import { getCurrentUser } from '../util/APIUtils';
import { ACCESS_TOKEN } from '../variables/constants';

//import EventList from '../views/HomePage/HomePage';
import Dashboard from '../views/Dashboard/Dashboard.jsx';
import Login from '../views/Login';

//import Signup from '../views/SignUpPage/SignUpPage';
//import Profile from '../views/ClubPage/ClubPage';
//import Event from '../views/EventPage/EventPage';
import NotFound from '../views/NotFound';
//import LoadingIndicator from '../components/LoadingIndicator/LoadingIndicator';
import PrivateRoute from './PrivateRoute';


// react plugin for creating notifications over the dashboard
import NotificationAlert from "react-notification-alert";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUser: null,
      isAuthenticated: true,//false,
      isLoading: true
    };
    //this.handleLogout = this.handleLogout.bind(this);
    this.loadCurrentUser = this.loadCurrentUser.bind(this);
    this.handleLogin = this.handleLogin.bind(this);
  }

  /*componentWillMount(){
    this.loadCurrentUser();
    alert(this.state.isAuthenticated);
  }*/


  loadCurrentUser() {
    this.setState({
      isLoading: true
    });
    getCurrentUser()
    .then(response => {
      this.setState({
        currentUser: response,
        isAuthenticated: true,
        isLoading: false
      });
      //alert(this.state.isAuthenticated);
    }).catch(error => {
      this.setState({
        isLoading: false
      });  
    });
  }

  componentDidMount() {
    this.loadCurrentUser();
  }

  /*handleLogout(redirectTo="/login", notificationType="success", description="Vous êtes déconnecté avec succès.") {
    localStorage.removeItem(ACCESS_TOKEN);

    this.setState({
      currentUser: null,
      isAuthenticated: false
    });
    //this.notify();

    this.props.history.push('/login');

    /*notification[notificationType]({
      message: 'Event App',
      description: description,
    });*
  }*/

  handleLogin() {
    this.notify("tr","success","Vous êtes connecté avec succès.");
    this.loadCurrentUser();
    this.props.history.push("/");
  }

  /********************** Notification *************************
  notify = () => {
    let place='tr';
    let type="success";
    let message="Vous êtes connecté avec succès.";
    let options = {};
    options = {
      place: place,
      message: (
          <div>
            <div>
              Welcome to <b>Black Dashboard React</b> - a beautiful freebie for
              every web developer.
            </div>
          </div>
      ),
      type: type,
      icon: "tim-icons icon-bell-55",
      autoDismiss: 3
    };
    this.refs.notificationAlert.notificationAlert(options);
  };*/
  handleLogout = (notificationType="success", description="")=> {
    localStorage.removeItem(ACCESS_TOKEN);

    this.setState({
      currentUser: null,
      isAuthenticated: false
    });
    this.notify("tr","info", "Vous êtes déconnecté avec succès.");
    this.props.history.push('/login');
  };
  notify = (place,type,message) => {

    let options = {};
    options = {
      place: place,
      message: (
          <div>
            <div>
              {message}
            </div>
          </div>
      ),
      type: type,
      icon: "tim-icons icon-bell-55",
      autoDismiss: 2
    };
    this.refs.notificationAlert.notificationAlert(options);
  };

  render() {
    /*if(this.state.isLoading) {
      return <LoadingIndicator />
    }*/
    return (
        <>
            <div className="react-notification-alert-container">
              <NotificationAlert ref="notificationAlert" />
            </div>
            <Switch>
              {/*<Route exact path="/"
                    render={(props) => <EventList isAuthenticated={this.state.isAuthenticated}
                        onLogout={this.handleLogout} {...props} />}>
                  </Route>*/}
              <Route path="/login"
                     render={(props) => <Login onLogin={this.handleLogin} {...props} />} />
              {/*<Route path="/signup" component={Signup} />*/}
              <PrivateRoute path="/user" component={Dashboard} handleLogout={this.handleLogout} history={this.props.history} currentUser={this.state.currentUser} isAuthenticated={this.state.isAuthenticated}/>

              <Route component={NotFound} />

            </Switch>
        </>
    );
  }
}

export default withRouter(App);
