import React from "react";

import { getTransfer } from '../../../util/APIUtils';
// react plugin used to create datetime
import { formatDateTime } from "../../../util/Helpers";

// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  FormGroup,
  Input,
  Label,
  Button
} from "reactstrap";
import NotFound from "../../NotFound";


/* Cette page présente un formulaire qui contient l'ensemble des informations demandées pour effectuer un virement */

class Forms extends React.Component {
  // Constructeur
  constructor(props) {
    super(props);
    this.state = {
      transfer: null,
      isLoading: true,
    };
    this.loadTransferDetails = this.loadTransferDetails.bind(this);
  }

  loadTransferDetails(id) {
    this.setState({
      isLoading: true
    });

    getTransfer(id)
        .then(response => {
          this.setState({
            transfer: response,
            isLoading: false
          });
        }).catch(error => {
      if(error.status === 404) {
        this.setState({
          notFound: true,
          isLoading: false
        });
      } else {
        this.setState({
          serverError: true,
          isLoading: false
        });
      }
    });
  }

  componentDidMount() {
    //const id = this.props.match.params.id;
    //this.loadTransferDetails(id);
  }

  render() {
    const { isLoading, transfer } = this.state;
    /*if(isLoading) {
      return <LoadingIndicator />;
    }*/

    if(this.state.notFound ) {
      return <NotFound />;
    }

    /*if(this.state.serverError) {
      return <ServerError />;
    }*/
    return (
        <>
          <Card>
            <CardBody>
              <form onSubmit={this.handleSubmit}>
                <div className="form-row">
                  <FormGroup className="col-md-6">
                    <Label for="principalAccount">N° compte débiteur</Label>
                    <h3 className="card-title">
                      {transfer.principalAccount+" "}<span>{transfer.principalCurrency}</span>
                    </h3>
                  </FormGroup>

                  <FormGroup className="col-md-6">
                    <Label for="beneficiaryAccount">N° compte bénéficiaire</Label>
                    <h3 className="card-title">
                      {transfer.beneficiaryAccount+" "}<span>{transfer.beneficiaryCurrency}</span>
                    </h3>
                  </FormGroup>
                </div>

                <div className="form-row">
                  <FormGroup className="col-md-4">
                    <Label for="transactionAmount">Montant</Label>
                    <h3 className="card-title">
                      {transfer.transactionAmount+" "}<span>{transfer.transactionCurrency}</span>
                    </h3>
                  </FormGroup>
                  <FormGroup className="col-md-4">
                    <Label for="transferReason">Motif</Label>
                    <Input
                        type="text"
                        placeholder="Motif ..."
                        id="transferReason"
                        value={this.state.transferReason}
                        onChange={this.handleChange}
                    />
                    <h3 className="card-title">{transfer.transferReason}</h3>
                  </FormGroup>
                  <FormGroup className="col-md-4">
                    <Label for="executionDate">Date d'execution</Label>
                    <h3 className="card-title">{formatDateTime(transfer.executionDate)}</h3>
                  </FormGroup>
                </div>
                {/********************* Echange **************/
                  (transfer.principalCurrency==="MAD" && transfer.beneficiaryCurrency!=="MAD") ?(
                 <>
                   {
                     (transfer.transactionAmount>transfer.largeAmount)?(
                         <FormGroup>
                           <Label for="tradingTicket">Ticket de négociation</Label>
                           <h3 className="card-title">{transfer.tradingTicket}</h3>
                         </FormGroup>
                     ):null
                   }
                   <FormGroup>
                    <Label for="transferNature">Nature de transfert</Label>
                     <h3 className="card-title">{transfer.transferNature}</h3>
                  </FormGroup>

                  <div className="form-row">
                    <FormGroup className="col-md-3">
                      <Label for="authorizationNumber">N° d'autorisation</Label>
                      <h3 className="card-title">{transfer.authorizationNumber}</h3>
                    </FormGroup>
                    <FormGroup className="col-md-6">
                      <Label for="authorizationValidity">Validité de l'autorisation</Label>
                      <h3 className="card-title">{transfer.authorizationValidity}</h3>
                    </FormGroup>
                    <FormGroup className="col-md-3">
                      <Label for="exchangeRate">Cours de change</Label>
                      <h3 className="card-title">{transfer.exchangeRate}</h3>
                    </FormGroup>
                   </div>
                 </>):null
                  /********************* Commission **************/}
                <FormGroup>
                  <Label for="chargeType">Type de charge</Label>
                  <h3 className="card-title">{transfer.chargeType}</h3>
                </FormGroup>

                <div className="form-row">
                  <FormGroup className="col-md-4">
                    <Label for="commissionCode">Taux applique</Label>
                    <h3 className="card-title">{transfer.applyedRate+" "}<span></span></h3>
                  </FormGroup>

                  <FormGroup className="col-md-4">
                    <Label for="commissionAmount">Montant commission</Label>
                    <h3 className="card-title">
                      {transfer.commissionAmount+" "}<span></span>
                    </h3>
                  </FormGroup>

                  <FormGroup className="col-md-4">
                    <Label for="commissionTVA">TVA sur commission</Label>
                    <h3 className="card-title">
                      {transfer.commissionTVA+" "}<span></span>
                    </h3>
                  </FormGroup>
                </div>

                <FormGroup check>
                  <Label for="totalAmount">Montant total</Label>
                  <h3 className="card-title">
                    {transfer.totalAmount+" "}<span>{transfer.transactionCurrency}</span>
                  </h3>
                </FormGroup>
                <FormGroup check>
                  <Label className="form-check-label">Appliquer commission</Label>
                  <h3 className="card-title">
                    {transfer.applyCommission?("Oui"):"Non"}
                  </h3>
                </FormGroup>

                <br />
                <Button  type="reset" color="primary">Annuler</Button>
                <Button type="submit" color="primary">Virer</Button>
              </form>
            </CardBody>
          </Card>
        </>
    );
  }
};

