import React from "react";
import { Link } from "react-router-dom";


import _ from "lodash";
import { makeData } from "./Utils";

// Import React Table
import ReactTable from "react-table";
//import "react-table/react-table.css";
import "../../../assets/css/react-table.css";






// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Table,
  Row,
  Col,
  Button
} from "reactstrap";

import { connect } from "react-redux";
import { getCurrentUser } from "../../../redux/actions";
// Sweet alert
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

import {TRANSFER_LIST_SIZE} from "../../../variables/constants";
import { getAllTransfers, cancelTransfer, acceptTransfer, sendTransfer} from "../../../util/APIUtils";
import NotFound from "./TransferPage";
import {formatDateTime} from "../../../util/Helpers";
import LoadingIndicator from "../../../components/LoadingIndicator/LoadingIndicator";


const MySwal = withReactContent(Swal);


//const rawData = makeData();

class TransfersTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      transfers: [],
      page: 0,
      size: 10,
      totalElements: 0,
      totalPages: 0,
      last: true,
      isLoadingTransfers: true,





      data: [],
      pages: null,
      loading: true
    };
    this.loadTransfersList = this.loadTransfersList.bind(this);
    //this.handleLoadMore = this.handleLoadMore.bind(this);
    this.handleCancelTransfer = this.handleCancelTransfer.bind(this);
    this.handleAcceptTransfer = this.handleAcceptTransfer.bind(this);
    this.handleSendTransfer = this.handleSendTransfer.bind(this);


    this.fetchData = this.fetchData.bind(this);
  }

  componentDidMount() {
    this.props.getCurrentUser();
    /*************************************************/
    //const username = this.props.match.params.username;
    this.loadTransfersList();
  }

  componentDidUpdate(nextProps) {
    /*if(this.props.isAuthenticated !== nextProps.isAuthenticated) {
      // Reset State
      this.setState({
        transfers: [],
        page: 0,
        size: 10,
        totalElements: 0,
        totalPages: 0,
        last: true,
        isLoadingTransfers: false
      });
      this.loadTransfersList();
    }*/
  }

  handleLoadMore() {
    this.loadTransfersList(this.state.page + 1);
  }

  loadTransfersList(page = 0, size = TRANSFER_LIST_SIZE) {
    let promise;

    promise = getAllTransfers();//getUserCreatedTransfers(this.props.currentUser.username, page, size);

    if(!promise) {
      return;
    }

    this.setState({
      isLoadingTransfers: true
    });

    promise
        .then(response => {
          this.setState({
            transfers: response,/*response.content
            page: response.page,
            size: response.size,
            totalElements: response.totalElements,
            totalPages: response.totalPages,
            last: response.last,*/
            isLoadingTransfers: false
          })
        }).catch(error => {
      this.setState({
        isLoadingTransfers: false
      })
    });

  }

  handleCancelTransfer(id) {
    MySwal.fire({
      buttonsStyling:false,
      customClass: {
        confirmButton: 'btn btn-success',
        cancelButton: 'btn btn-danger',
      },
      title: 'Entrer votre motif',
      input: 'textarea',
      inputPlaceholder: 'Votre motif ...',
      showCancelButton: true,
      confirmButtonText: 'Confirmer',
      cancelButtonText: 'Annuler'
    }).then((result) => {
      if (result.value) {
        let reason = result.value;
        cancelTransfer(id, reason)
            .then(response => {

              /*return */MySwal.fire({
                type: 'success',
                title: 'Votre operation a été enregistrée',
                showConfirmButton: false,
                timer: 1500
              });
              this.loadTransfersList();
            }).catch(error => {
          if(error.status === 401) {
            MySwal.fire({
              type: 'error',
              title: 'Vous avez été déconnecté. Veuillez vous connecter pour effectuer cette opération.',
              showConfirmButton: false,
              timer: 1500
            });
            this.props.history.push('/login');
          } else {
            MySwal.fire({
              type: 'warning',
              title: error.message || 'Pardon! Quelque chose s\'est mal passé. Veuillez réessayer!',
              showConfirmButton: false,
              timer: 1500
            })
          }
        });
      }
    })
  }

  handleAcceptTransfer(id) {
    acceptTransfer(id)
        .then(response => {

          /*return */MySwal.fire({
            type: 'success',
            title: 'Votre operation a été enregistrée',
            showConfirmButton: false,
            timer: 1500
          });
          this.loadTransfersList();
        }).catch(error => {
      if(error.status === 401) {
        MySwal.fire({
          type: 'error',
          title: 'Vous avez été déconnecté. Veuillez vous connecter pour effectuer cette opération.',
          showConfirmButton: false,
          timer: 1500
        });
        this.props.history.push('/login');
      } else {
        MySwal.fire({
          type: 'warning',
          title: error.message || 'Pardon! Quelque chose s\'est mal passé. Veuillez réessayer!',
          showConfirmButton: false,
          timer: 1500
        });
      }
    });
  }

  handleSendTransfer(id) {
    sendTransfer(id)
        .then(response => {

          /*return */MySwal.fire({
            type: 'success',
            title: 'Votre operation a été enregistrée',
            showConfirmButton: false,
