import React from "react";

// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Table,
  Row,
  Col,
    Button
} from "reactstrap";
// Sweet alert
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

import {TRANSFER_LIST_SIZE} from "../../../variables/constants";
import {getUserCreatedTransfers,  getAllTransfers, cancelledTransfer} from "../../../util/APIUtils";
import NotFound from "./TransferPage";


const MySwal = withReactContent(Swal);

class Tables extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      transfers: [],
      page: 0,
      size: 10,
      totalElements: 0,
      totalPages: 0,
      last: true,
      isLoadingTransfers: true
    };
    this.loadTransfersList = this.loadTransfersList.bind(this);
    this.handleLoadMore = this.handleLoadMore.bind(this);
    this.handleCancelledTransfer = this.handleCancelledTransfer.bind(this);
  }

  componentDidMount() {
    /*************************************************/
    //const username = this.props.match.params.username;
    this.loadTransfersList();
  }

  componentDidUpdate(nextProps) {
    if(this.props.isAuthenticated !== nextProps.isAuthenticated) {
      // Reset State
      this.setState({
        transfers: [],
        motif:"test",
        page: 0,
        size: 10,
        totalElements: 0,
        totalPages: 0,
        last: true,
        isLoadingTransfers: false
      });
      this.loadTransfersList();
    }
  }

  handleLoadMore() {
    this.loadTransfersList(this.state.page + 1);
  }

  loadTransfersList(page = 0, size = TRANSFER_LIST_SIZE) {
    let promise;

    promise = getAllTransfers();//getUserCreatedTransfers(this.props.currentUser.username, page, size);

    if(!promise) {
      return;
    }

    this.setState({
      isLoadingTransfers: true
    });

    promise
        .then(response => {
          this.setState({
            transfers: response,/*response.content
            page: response.page,
            size: response.size,
            totalElements: response.totalElements,
            totalPages: response.totalPages,
            last: response.last,*/
            isLoadingTransfers: false
          })
        }).catch(error => {
      this.setState({
        isLoadingTransfers: false
      })
    });

  }

  handleCancelledTransfer(id) {
    MySwal.fire({
      buttonsStyling:false,
      customClass: {
        confirmButton: 'btn btn-success',
        cancelButton: 'btn btn-danger',
      },
      title: 'Enter your motif',
      input: 'textarea',
      inputPlaceholder: 'Votre motif ...',
      showCancelButton: true,
      confirmButtonText: 'Yes, delete it!'
    }).then((result) => {
      //result.value;
      /*this.setState({
        motif: result.value
      });*/
      /*return MySwal.fire({
        type: 'success',
        title: 'Your work has been saved',
        showConfirmButton: false,
        timer: 1500
      })*/
      if (result.value) {
        cancelledTransfer(id)
            .then(response => {

              /*return */MySwal.fire({
                type: 'success',
                title: 'Votre operation a été enregistré',
                showConfirmButton: false,
                timer: 1500
              });
              this.loadTransfersList();
            }).catch(error => {
          if(error.status === 401) {
            this.props.handleLogout('/login', 'error', 'Vous avez été déconnecté. Veuillez vous connecter pour effectuer cette opération.');
            /*MySwal.fire({
              type: 'error',
              title: 'Vous avez été déconnecté. Veuillez vous connecter pour effectuer cette opération.',
              showConfirmButton: false,
              timer: 1500
            })*/
          } else {
            this.props.notify('tr','warning',error.message || 'Pardon! Quelque chose s\'est mal passé. Veuillez réessayer!');
            /*return MySwal.fire({
              type: 'error',
              title: error.message || 'Pardon! Quelque chose s\'est mal passé. Veuillez réessayer!',
              showConfirmButton: false,
              timer: 1500
            })*/
          }
        });
      }
    })
  }

  renderSwitch(state, link) {
    switch(state) {
      case 'accepted':
        return <Button className="btn-link" href={link} color="success">Validée</Button>;
      case 'in_progress':
        return <Button className="btn-link" href={link} color="warning">Encours</Button>;
      case 'cancelled':
        return <Button className="btn-link" href={link} color="danger">Annulée</Button>;
      default:
        return null;
    }
  }

  renderBtn(link) {
    return(
        <>
          <Button className="btn-icon btn-simple" color="success" size="sm">
            <i className="fa fa-check"></i>
          </Button>{` `}
          <Button className="btn-icon btn-simple" onClick={this.handleCancelledTransfer} color="danger" size="sm">
            <i className="fa fa-times" />
          </Button>{` `}
          <Button className="btn-icon btn-simple" href={link} color="info" size="sm">
            <i className="fa fa-eye"></i>
          </Button>
        </>
    );
  }

  render() {
    const { isLoadingTransfers, transfers } = this.state;
    if(isLoadingTransfers) {
      return <h1>Waiting</h1>;
    }

    if(this.state.notFound ) {
      return <NotFound />;
    }

    /*if(this.state.serverError) {
      return <ServerError />;
    }*/
    return (
      <>
        <div className="content">
          <Row>
            <Col md="12">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Mes virements</CardTitle>
                </CardHeader>
                <CardBody>
                  <Table className="tablesorter" responsive>
                    <thead className="text-primary">
                      <tr>
                        <th>Référence</th>
                        <th>Date d'opération</th>
                        <th>Donneur d’ordre</th>
                        <th>Bénéficiaire</th>
                        <th className="text-center">Montant</th>
                        <th className="text-center">Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {transfers.map((transfer, key) => {
                        return (
                            <tr key={key}>
                              <td>{transfer.ref}</td>
                              <td>{transfer.date}</td>
                              <td>{transfer.principalAccount}</td>
                              <td>{transfer.beneficiaryAccount}</td>
                              <td className="text-center">{transfer.transactionAmount}</td>
                              <td className="text-center">
                                {/*this.renderSwitch(transfer.state,"/user/transfers/"+transfer.id)*/}
                                {this.renderBtn("/user/transfers/"+transfer.id)}
                              </td>
                            </tr>
                        );
                      })}
                    </tbody>
                  </Table>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

export default Tables;
