import React from "react";

// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Table,
  Row,
  Col,
    Button
} from "reactstrap";

import {TRANSFER_LIST_SIZE} from "../../../variables/constants";
import {getUserCreatedTransfers} from "../../../util/APIUtils";

class Tables extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      transfers: [],
      page: 0,
      size: 10,
      totalElements: 0,
      totalPages: 0,
      last: true,
      isLoadingTransfers: true
    };
    this.loadTransferList = this.loadTransferList.bind(this);
    this.handleLoadMore = this.handleLoadMore.bind(this);
    //this.handleDeleteEvent = this.handleDeleteEvent.bind(this);
  }

  loadTransferList(page = 0, size = TRANSFER_LIST_SIZE) {
    let promise;

    promise = getUserCreatedTransfers(this.props.currentUser.username, page, size);

    if(!promise) {
      return;
    }

    this.setState({
      isLoadingEvents: true
    });

    promise
        .then(response => {
          //const events = this.state.events.slice();
          //const currentReactions = this.state.currentReactions.slice();

          this.setState({
            events: response.content,//events.concat(response.content),
            page: response.page,
            size: response.size,
            totalElements: response.totalElements,
            totalPages: response.totalPages,
            last: response.last,
            //currentReactions: currentReactions.concat(Array(response.content.length).fill(null)),
            isLoadingEvents: false
          })
        }).catch(error => {
      this.setState({
        isLoadingEvents: false
      })
    });

  }

  render() {
    return (
      <>
        <div className="content">
          <Row>
            <Col md="12">
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Mes virements</CardTitle>
                </CardHeader>
                <CardBody>
                  <Table className="tablesorter" responsive>
                    <thead className="text-primary">
                      <tr>
                        <th>Référence</th>
                        <th>Date d'opération</th>
                        <th>Donneur d’ordre</th>
                        <th>Bénéficiaire</th>
                        <th className="text-center">Montant</th>
                        <th className="text-center">état</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>r12345</td>
                        <td>01/04/2019</td>
                        <td>15141312</td>
                        <td>12131415</td>
                        <td className="text-center">36,738$</td>
                        <td className="text-center"><Button className="btn-link" color="warning">
                          Encore
                        </Button></td>
                      </tr>
                      <tr>
                        <td>r12346</td>
                        <td>01/04/2019</td>
                        <td>12131415</td>
                        <td>15141312</td>
                        <td className="text-center">36,738$</td>
                        <td className="text-center"><Button className="btn-link" color="danger">
                          Annulé
                        </Button></td>
                      </tr>
                      <tr>
                        <td>r12347</td>
                        <td>01/04/2019</td>
                        <td>12131415</td>
                        <td>12131415</td>
                        <td className="text-center">23,789MAD</td>
                        <td className="text-center"><Button className="btn-link" color="success">
                          Validé
                        </Button></td>
                      </tr>
                    </tbody>
                  </Table>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

export default Tables;
