/* eslint-disable */
import React from "react";
import PropTypes from "prop-types";
import { Switch, Route } from "react-router-dom";
// creates a beautiful scrollbar
import PerfectScrollbar from "perfect-scrollbar";
import "perfect-scrollbar/css/perfect-scrollbar.css";
// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";
// core components
import Header from "components/Header/Header1.jsx";
import Footer from "components/Footer/Footer.jsx";
import Sidebar from "components/Sidebar/Sidebar.jsx";

import dashboardRoutesClub from "routes/dashboardAdmin.jsx";
import dashboardRoutesUser from "routes/dashboardUser.jsx";

import dashboardStyle from "assets/jss/material-dashboard-react/layouts/dashboardStyle.jsx";

import image from "assets/img/sidebar-2.jpg";
import logo from "assets/img/reactlogo.png";
import {getCurrentUser} from "../../util/APIUtils";
import Notifications from "@material-ui/icons/Notifications";

import DashboardPage from "views/Dashboard/Sections/Dashboard.jsx";
import Profile from "views/Dashboard/Sections/Profile.jsx";
import AddEvent from "views/Dashboard/Sections/AddEvent.jsx";
import EditEvent from "views/Dashboard/Sections/EditEvent.jsx";
import EventList from "views/Dashboard/Sections/EventList.jsx";
import ClubList from "views/Dashboard/Sections/ClubList.jsx";
import NotificationsPage from "views/Notifications/Notifications.jsx";
import NotFound from "../NotFound/NotFound";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        user: null,
        currentUser: this.props.currentUser,
        isLoading: true,
      mobileOpen: false
    };
    this.resizeFunction = this.resizeFunction.bind(this);
    this.loadUserProfile = this.loadUserProfile.bind(this);
  }
    loadUserProfile() {
        this.setState({
            isLoading: true
        });

        getCurrentUser()
            .then(response => {
                this.setState({
                    user: response,
                    isLoading: false
                });
            }).catch(error => {
            if(error.status === 404) {
                this.setState({
                    notFound: true,
                    isLoading: false
                });
            } else {
                this.setState({
                    serverError: true,
                    isLoading: false
                });
            }
        });
    }
  handleDrawerToggle = () => {
    this.setState({ mobileOpen: !this.state.mobileOpen });
  };
  getRoute() {
    return this.props.location.pathname !== "/maps";
  }
  resizeFunction() {
    if (window.innerWidth >= 960) {
      this.setState({ mobileOpen: false });
    }
  }
  componentDidMount() {
    if (navigator.platform.indexOf("Win") > -1) {
      const ps = new PerfectScrollbar(this.refs.mainPanel);
    }
    window.addEventListener("resize", this.resizeFunction);
      this.loadUserProfile();
  }
  componentDidUpdate(e) {
    if (e.history.location.pathname !== e.location.pathname) {
      this.refs.mainPanel.scrollTop = 0;
      if (this.state.mobileOpen) {
        this.setState({ mobileOpen: false });
      }
    }
      //this.loadUserProfile();
  }
  componentWillUnmount() {
    window.removeEventListener("resize", this.resizeFunction);
  }

  render() {
    const { classes, ...rest} = this.props;

    return (
      <div className={classes.wrapper}>

        <div className={classes.mainPanel} ref="mainPanel">
            <Header
                routes={dashboardRoutes}
                handleDrawerToggle={this.handleDrawerToggle}
                handleLogout={this.props.handleLogout}
                {...rest}
            />
          {/* On the /maps route we want the map to be on full screen - this is not possible if the content and conatiner classes are present because they have some paddings which would make the map smaller */}
          {this.getRoute() ? (
            <div className={classes.content}>
              <div className={classes.container}>{switchRoutes}</div>
            </div>
          ) : (
            <div className={classes.map}>{switchRoutes}</div>
          )}
          {this.getRoute() ? <Footer /> : null}
        </div>
      </div>
    );
  }
}

Dashboard.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(dashboardStyle)(Dashboard);
