import React, { Component } from 'react';
import AdminLayout from "layouts/Admin/Dashboard.jsx";
import {
  Route,
  withRouter,
  Switch,
  Redirect
} from 'react-router-dom';

import { getCurrentUser,login } from '../util/APIUtils';
import { ACCESS_TOKEN } from '../variables/constants';

import Dashboard from '../layouts/Admin/Dashboard.jsx';
import Login from '../views/Login';
import NotFound from '../views/NotFound';
//import LoadingIndicator from '../components/LoadingIndicator/LoadingIndicator';
import PrivateRoute from './PrivateRoute';


// react plugin for creating notifications over the dashboard
import NotificationAlert from "react-notification-alert";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUser: null,
      isAuthenticated: true,//false,
      isLoading: true
    };
    this.handleLogout = this.handleLogout.bind(this);
    this.loadCurrentUser = this.loadCurrentUser.bind(this);
    this.handleLogin = this.handleLogin.bind(this);
    this.notify = this.notify.bind(this);
  }

  /*componentWillMount(){
    this.loadCurrentUser();
    alert(this.state.isAuthenticated);
  }*/


  loadCurrentUser() {
    this.setState({
      isLoading: true
    });
    getCurrentUser()
    .then(response => {
      this.setState({
        currentUser: response,
        isAuthenticated: true,
        isLoading: false
      });
      //alert(this.state.isAuthenticated);
    }).catch(error => {
      this.setState({
        isLoading: false
      });  
    });
  }

  componentDidMount() {
    this.loadCurrentUser();
  }


  handleLogin = (loginRequest) => {

    //let loginRequest=null;
    login(loginRequest)
        .then(response => {
          localStorage.setItem(ACCESS_TOKEN, response.accessToken);
          this.notify("tr","success","Vous êtes connecté avec succès.");
          //this.loadCurrentUser();
          this.props.history.push("/user/transfer");
        }).catch(error => {
      if(error.status === 401) {
        this.notify("tr","danger","Votre nom d'utilisateur ou votre mot de passe est incorrect. Veuillez réessayer!");
      } else {
        this.setState({
          isAuthenticated: true
        });
        this.props.history.push("/user/transfer");
        this.notify("tr","warning","Pardon! Quelque chose s'est mal passé. Veuillez réessayer!");
      }
    });
  };



  handleLogout = ()=> {
    localStorage.removeItem(ACCESS_TOKEN);

    this.setState({
      currentUser: null,
      isAuthenticated: false
    });
    this.notify("tr","success", "Vous êtes déconnecté avec succès.");
    this.props.history.push('/login');
  };

  notify = (place ="tr",type="danger", message="ok") => {

    let options = {};
    options = {
      place: place,
      message: (
          <div>
            <div>
              {message}
            </div>
          </div>
      ),
      type: type,
      icon: "tim-icons icon-bell-55",
      autoDismiss: 2
    };
    this.refs.notificationAlert.notificationAlert(options);
  };

  render() {
    /*if(this.state.isLoading) {
      return <LoadingIndicator />
    }*/
    return (
        <>
            <div className="react-notification-alert-container">
              <NotificationAlert ref="notificationAlert" />
            </div>
            <Switch>
              <Route path="/login"
                     render={(props) => <Login handleLogin={this.handleLogin} {...props} />} />
              <PrivateRoute path="/user" component={Dashboard} handleLogout={this.handleLogout} notify={(place,type,message)=>this.notify(place,type,message)} history={this.props.history} currentUser={this.state.currentUser} isAuthenticated={this.state.isAuthenticated}/>
              <Redirect exact from="/" to="/login" />
              <Route component={NotFound} />
            </Switch>
        </>
    );
  }
}

export default withRouter(App);
