import React from "react";

import {
  getAccountData, getAccountCurrency, createTransfer, getCommissionData
} from '../../util/APIUtils';
// react plugin used to create datetimepicker
import ReactDatetime from "react-datetime";

// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  FormGroup,
  Input,
  Label,
  Button
} from "reactstrap";

import "bootstrap/dist/css/bootstrap.css";

class Forms extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      focused: "",
      transactionType: "",
      principalAccount: "",
      principalCurrency: "NaN",
      beneficiaryAccount: "",
      beneficiaryCurrency: "NaN",
      transactionAmount: 0,
      transactionCurrency: "NaN",
      transferReason: "",
      executionDate: new Date(),
      DBValueDate: "",
      CRValueDate: "",
      transferNature: "",
      authorizationNumber: "",
      authorizationValidity: "",
      //commissionCode: 0,
      applyedRate:0,
      commissionAmount: 0,
      applyCommission: true,
      commissionTVA: 0,
      chargeType: "",
      exchangeRate: "",
      tradingTicket: "",
      largeAmount: 4000,
      totalAmount:2000,

      account: [],
      /*page: 0,
      size: 10,
      totalElements: 0,
      totalPages: 0,
      last: true,*/
      isLoadingAccount: true
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    //this.loadAccountData = this.loadAccountData.bind(this);
    this.loadCommissionData = this.loadCommissionData.bind(this);
    this.loadAccountCurrency = this.loadAccountCurrency.bind(this);
    this.getTotalAmount = this.getTotalAmount.bind(this);

  }

  handleChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const id = target.id;

    //console.log(id+":"+value);

    this.setState({
      [id]: value
    });
  }

  handleSubmit(event) {
    event.preventDefault();

    const transferData = {
        transactionType: this.state.transactionType,
        principalAccount: this.state.principalAccount,
        principalCurrency: this.state.principalCurrency,
        beneficiaryAccount: this.state.beneficiaryAccount,
        beneficiaryCurrency: this.state.beneficiaryCurrency,
        transactionAmount: this.state.transactionAmount,
        transactionCurrency: this.state.transactionCurrency,
        transferReason: this.state.transferReason,
        executionDate: this.state.executionDate,
        transferNature: this.state.transferNature,
        authorizationNumber: this.state.authorizationNumber,
        authorizationValidity: this.state.authorizationValidity,
        //commissionCode: this.state.commissionCode,
        applyedRate: this.state.applyedRate,
        commissionAmount: this.state.commissionAmount,
        applyCommission: this.state.applyCommission,
        commissionTVA: this.state.commissionTVA,
        chargeType: this.state.chargeType,
        exchangeRate: this.state.exchangeRate,
        tradingTicket: this.state.tradingTicket,
        totalAmount: this.state.totalAmount
    };

    createTransfer(transferData)
        .then(response => {
          this.props.notify('tr','success','Virement effectué avec succès');
          this.props.history.push("/");
        }).catch(error => {
      if(error.status === 401) {
        this.props.handleLogout('/login', 'error', 'Vous avez été déconnecté. Veuillez vous connecter pour effectuer un virement.');
      } else {
        this.props.notify('tr','danger','Pardon! Quelque chose s\'est mal passé. Veuillez réessayer!');
      }
    });
  }

  /*loadAccountData(number) {
    let promise;

    promise = getAccountData(number);

    if(!promise) {
      return;
    }

    this.setState({
      isLoadingCurrency: true
    });

    promise
        .then(response => {
          this.setState({
            account: response.content,
            isLoadingCurrency: false
          })
        }).catch(error => {
      this.setState({
        isLoadingCurrency: false
      })
    });
  }*/

  loadAccountCurrency = (event) => {
    const number = event.target.value;
    const id = event.target.id;

    if(number.length===7){
      let idCurrency = (id==="principalAccount")?("principalCurrency"):("beneficiaryCurrency");
      let promise;

      promise = getAccountCurrency(number);

      if(!promise) {
        return;
      }

      this.setState({
        isLoadingCurrency: true
      });

      promise
          .then(response => {
            this.setState({
              [idCurrency]: response.content,
              isLoadingCurrency: false
            })
          }).catch(error => {
        this.setState({
          isLoadingCurrency: false
        })
      });
    }
  };

  loadCommissionData = (event) => {
    event.preventDefault();

    let commissionData = {
      accountN1 : this.state.principalAccount,
      accountN2 : this.state.beneficiaryAccount,
      amount : this.state.totalAmount
    };

    let promise;

    promise = getCommissionData(commissionData);

    if(!promise) {
      return;
    }

    this.setState({
      isLoadingCurrency: true
    });

    promise
        .then(response => {
          this.setState({
            applyedRate: response.applyedRate,
            commissionAmount: response.commissionAmount,
            commissionTVA: response.commissionTVA,
            //isLoadingCurrency: false
          })
        }).catch(error => {
      this.setState({
        //isLoadingCurrency: false
      })
    });
  };

  getTotalAmount(){
    if(this.state.applyCommission){
     /* this.setState({
        totalAmount: parseFloat(this.state.transactionAmount)+this.state.commissionAmount+this.state.commissionTVA
      });*/
      return /*this.totalAmount;//=*/parseFloat(this.state.transactionAmount)+this.state.commissionAmount+this.state.commissionTVA;
    }
    else{
      /*this.setState({
        totalAmount: this.state.principalCurrency
      });*/
      return /*this.totalAmount;//=*/this.state.transactionAmount;
    }
  }

  getTransactionCurrency(){
    if(this.state.principalCurrency===this.state.beneficiaryCurrency){
      this.setState({
        transactionCurrency: this.state.principalCurrency
      })
    }
  }

  render() {
    return (
        <>
          <Card>
            <CardBody>
              <form onSubmit={this.handleSubmit}>
                {/*<FormGroup>
                  <Label for="transactionType">Type de transaction</Label>
                  <Input
                      type="select"
                      name="select"
                      id="transactionType"
                      value={this.state.transactionType}
                      onChange={this.handleChange}
                  >
                    <option selected>1</option>
                    <option>2</option>
                  </Input>
                </FormGroup>*/}

                <div className="form-row">
                  <FormGroup className="col-md-6">
                    <Label for="principalAccount">N° compte débiteur</Label>
                    <InputGroup>
                      <Input
                          type="number"
                          id="principalAccount"
                          placeholder="Débiteur"
                          value={this.state.principalAccount}
                          onChange={(event)=>{this.handleChange(event);this.loadAccountCurrency(event)}}
                      />
                      <InputGroupAddon addonType="append">
                        <InputGroupText>
                          <p>{this.state.principalCurrency}</p>
                        </InputGroupText>
                      </InputGroupAddon>
                    </InputGroup>
                  </FormGroup>
                  <FormGroup className="col-md-6">
                    <Label for="beneficiaryAccount">N° compte bénéficiaire</Label>
                    <InputGroup>
                      <Input
                          type="number"
                          id="beneficiaryAccount"
                          placeholder="Bénéficiaire"
                          value={this.state.beneficiaryAccount}
                          onChange={(event)=>{this.handleChange(event);this.loadAccountCurrency(event)}}
                      />
                      <InputGroupAddon addonType="append">
                        <InputGroupText>
                          <p>{this.state.beneficiaryCurrency}</p>
                        </InputGroupText>
                      </InputGroupAddon>
                    </InputGroup>
                  </FormGroup>
                </div>

                <div className="form-row">
                  <FormGroup className="col-md-4">
                    <Label for="transactionAmount">Montant</Label>
                    <InputGroup>
                      <Input
                          type="number"
                          id="transactionAmount"
                          placeholder="Montant ..."
                          value={this.state.transactionAmount}
                          onChange={(event)=>{this.handleChange(event);this.getTransactionCurrency();this.loadCommissionData(event)}}
                      />
                      <InputGroupAddon addonType="append">
                        <InputGroupText>
                          {this.state.principalCurrency===this.state.beneficiaryCurrency?
                              (<p>{this.state.transactionCurrency}</p>):
                              (<select
                                  name="select"
                                  className="form-control1"
                                  id="transactionCurrency"
                                  value={this.state.transactionCurrency}
                                  onChange={this.handleChange}
                              >
                                <option selected>{this.state.principalCurrency}</option>
                                <option>{this.state.beneficiaryCurrency}</option>
                              </select>)
                          }
                        </InputGroupText>
                      </InputGroupAddon>
                    </InputGroup>
                  </FormGroup>
                  <FormGroup className="col-md-4">
                    <Label for="transferReason">Motif</Label>
                    <Input
                        type="text"
                        placeholder="Motif ..."
                        id="transferReason"
                        value={this.state.transferReason}
                        onChange={this.handleChange}
                    />
                  </FormGroup>
                  <FormGroup className="col-md-4">
                    <Label for="executionDate">Date d'execution</Label>
                    <ReactDatetime
                        id="executionDate"
                        inputProps={{
                          className: "form-control",
                          placeholder: "Date Picker Here"
                        }}
                        value={this.state.executionDate}
                        defaultValue={new Date()}
                    />
                  </FormGroup>
                </div>
                {/********************* Echange **************/
                  (this.state.principalCurrency==="MAD" && this.state.beneficiaryCurrency!=="MAD") ?(
                 <>
                   {
                     (this.state.transactionAmount>this.state.largeAmount)?(
                         <FormGroup>
                           <Label for="tradingTicket">Ticket de négociation</Label>
                           <Input
                               type="number"
                               id="tradingTicket"
                               placeholder="ticket ..."
                               className="tim-icons"
                               value={this.state.tradingTicket}
                               onChange={this.handleChange}
                           />
                         </FormGroup>
                     ):null
                   }
                   <FormGroup>
                    <Label for="transferNature">Nature de transfert</Label>
                    <Input
                        type="select"
                        name="select"
                        id="transferNature"
                        value={this.state.transferNature}
                        onChange={this.handleChange}
                    >
                      <option selected>1</option>
                      <option>2</option>
                    </Input>
                  </FormGroup>

                  <div className="form-row">
                    <FormGroup className="col-md-3">
                      <Label for="authorizationNumber">N° d'autorisation</Label>
                      <Input
                          type="number"
                          id="authorizationNumber"
                          placeholder="Code ..."
                          value={this.state.authorizationNumber}
                          onChange={this.handleChange}
                      />
                    </FormGroup>
                    <FormGroup className="col-md-6">
                      <Label for="authorizationValidity">Validité de l'autorisation</Label>
                      <Input
                          type="number"
                          id="authorizationValidity"
                          placeholder="???"
                          value={this.state.authorizationValidity}
                          onChange={this.handleChange}
                      />
                    </FormGroup>
                    <FormGroup className="col-md-3">
                      <Label for="exchangeRate">Cours de change</Label>
                      <Input
                          type="number"
                          id="exchangeRate"
                          placeholder="..."
                          value={this.state.exchangeRate}
                          disabled
                      />
                    </FormGroup>
                   </div>
                 </>):null
                  /********************* Commission **************/}
                <FormGroup>
                  <Label for="chargeType">Type de charge</Label>
                  <Input
                      type="select"
                      id="chargeType"
                      name="select"
                      value={this.state.chargeType}
                      onChange={this.handleChange}
                  >
                    <option selected>OUR</option>
                    <option>2</option>
                  </Input>
                </FormGroup>

                <div className="form-row">
                  <FormGroup className="col-md-4">
                    <Label for="commissionCode">{/*Code Commission/*/}Taux applique</Label>
                    {/*<Input
                        type="number"
                        id="commissionCode"
                        placeholder="Code ..."
                        value={this.state.commissionCode}
                        disabled
                    />*/}
                    <h3 className="card-title">
                      {this.state.applyedRate+" "}<span></span>
                    </h3>
                  </FormGroup>

                  <FormGroup className="col-md-4">
                    <Label for="commissionAmount">Montant commission</Label>
                    {/*<Input
                        type="number"
                        id="commissionAmount"
                        placeholder="MAD"
                        value={this.state.commissionAmount}
                        disabled
                    />*/}
                    <h3 className="card-title">
                      {this.state.commissionAmount+" "}<span></span>
                    </h3>
                  </FormGroup>

                  <FormGroup className="col-md-4">
                    <Label for="commissionTVA">TVA sur commission</Label>
                    {/*<Input
                        type="number"
                        id="commissionTVA"
                        placeholder="tva ..."
                        value={this.state.commissionTVA}
                        disabled
                    />*/}
                    <h3 className="card-title">
                      {this.state.commissionTVA+" "}<span></span>
                    </h3>
                  </FormGroup>

                </div>

                {/*<FormGroup>
                  <Label for="totalAmount">Montant total</Label>
                  <Input
                      type="number"
                      id="totalAmount"
                      placeholder="..."
                      value={this.state.totalAmount}
                      disabled
                  />
                </FormGroup>*/}

                <FormGroup check>
                  <Label for="totalAmount">Montant total</Label>
                  <h3 className="card-title">
                    {this.getTotalAmount()+" "}<span>{this.state.transactionCurrency}</span>
                  </h3>
                </FormGroup>

                {this.state.totalAmount}
                <FormGroup check>
                  <Label className="form-check-label">
                    <Input
                        className="form-check-input"
                        type="checkbox"
                        id="applyCommission"
                        onChange={this.handleChange}
                        checked={this.state.applyCommission}
                    />
                    Appliquer commission
                    <span className="form-check-sign">
                      <span className="check"></span>
                    </span>
                  </Label>
                </FormGroup>

                <br />
                <Button  type="reset" color="primary">Annuler</Button>
                <Button type="submit" color="primary">Virer</Button>
              </form>
            </CardBody>
          </Card>
        </>
    );
  }
};


class Transfer extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
        <>
          <div className="content">
            <Row>
              <Col md="12">
                <Card>
                  <CardHeader>
                    <h4 className="title">Saisie du virement</h4>
                  </CardHeader>
                  <CardBody>
                    <Row>
                      <Col md="2"></Col>
                      <Col md="8">
                        {/* Virement INTRA en agence*/}
                        <Forms notify={(place,type,message) => this.props.notify(place,type,message)} />
                      </Col>
                      <Col md="2"></Col>
                    </Row>
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </div>
        </>
    );
  }
}
export default Transfer;
