import React from "react";

import {
  getAccountData
} from '../util/APIUtils';
// react plugin used to create datetimepicker
import ReactDatetime from "react-datetime";

// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  FormGroup,
  Input,
  Label,
  Button
} from "reactstrap";

import "bootstrap/dist/css/bootstrap.css";

class Forms extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      focused: "",
      transactionType: "",
      principalAccount: "",
      principalCurrency: "MAD",
      beneficiaryAccount: "",
      beneficiaryCurrency: "MAD",
      transactionAmount: "",
      transactionCurrency: "",
      transferReason: "",
      executionDate: "",
      DBValueDate: "",
      CRValueDate: "",
      transferNature: "",
      authorizationNumber: "",
      authorizationValidity: "",
      commissionCode: "",
      commissionAmount: "",
      applyCommission: true,
      commissionTVA: "",
      chargeType: "",
      exchangeRate: "",
      tradingTicket: "",
      largeAmount: 4000
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.loadAccountData = this.loadAccountData.bind(this);
  }

  loadAccountData(number) {
    let promise;

    promise = getAccountData(number);

    if(!promise) {
      return;
    }

    this.setState({
      isLoadingClubs: true
    });

    promise
        .then(response => {
          const clubs = this.state.clubs.slice();

          this.setState({
            clubs: clubs.concat(response.content),
            size: response.size,
            isLoadingClubs: false
          })
        }).catch(error => {
      this.setState({
