                   <FormGroup>
                    <Label for="transferNature">Nature de transfert</Label>
                    <Input
                        type="select"
                        name="select"
                        id="transferNature"
                        value={this.state.transferNature}
                        onChange={this.handleChange}
                    >
                      <option selected>1</option>
                      <option>2</option>
                    </Input>
                  </FormGroup>

                  <div className="form-row">
