import React, { Component } from 'react';
import {
  Route,
  withRouter,
  Switch
} from 'react-router-dom';

import { getCurrentUser } from '../util/APIUtils';
import { ACCESS_TOKEN } from '../variables/constants';

//import EventList from '../views/HomePage/HomePage';
//import Dashboard from '../views/Dashboard/Dashboard.jsx';
import Login from '../views/Login';
//import Signup from '../views/SignUpPage/SignUpPage';
//import Profile from '../views/ClubPage/ClubPage';
//import Event from '../views/EventPage/EventPage';
import NotFound from '../views/NotFound';
//import LoadingIndicator from '../components/LoadingIndicator/LoadingIndicator';
import PrivateRoute from './PrivateRoute';

//import { notification } from 'antd';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUser: null,
      isAuthenticated: false,
      isLoading: true
    };
    this.handleLogout = this.handleLogout.bind(this);
    this.loadCurrentUser = this.loadCurrentUser.bind(this);
    this.handleLogin = this.handleLogin.bind(this);

    /*notification.config({
      placement: 'topRight',
      top: 70,
      duration: 3,
    });*/
  }

  /*componentWillMount(){
    this.loadCurrentUser();
    alert(this.state.isAuthenticated);
  }*/


  loadCurrentUser() {
    this.setState({
      isLoading: true
    });
    getCurrentUser()
    .then(response => {
      this.setState({
        currentUser: response,
        isAuthenticated: true,
        isLoading: false
      });
      //alert(this.state.isAuthenticated);
    }).catch(error => {
      this.setState({
        isLoading: false
      });  
    });
  }

  componentDidMount() {
    this.loadCurrentUser();
  }

  handleLogout(redirectTo="/", notificationType="success", description="Vous êtes déconnecté avec succès.") {
    localStorage.removeItem(ACCESS_TOKEN);

    this.setState({
      currentUser: null,
      isAuthenticated: false
    });

    this.props.history.push('/');

    /*notification[notificationType]({
      message: 'Event App',
      description: description,
    });*/
  }

  handleLogin() {
    /*notification.success({
      message: 'Event App',
      description: "Vous êtes connecté avec succès.",
    });*/
    this.loadCurrentUser();
    this.props.history.push("/");
  }

  render() {
    /*if(this.state.isLoading) {
      return <LoadingIndicator />
    }*/
    return (
              <Switch>      
                {/*<Route exact path="/"
                  render={(props) => <EventList isAuthenticated={this.state.isAuthenticated}
                      currentUser={this.state.currentUser} handleLogout={this.handleLogout} onLogout={this.handleLogout} {...props} />}>
                </Route>*/}
                <Route path="/login"
                  render={(props) => <Login onLogin={this.handleLogin} {...props} />} />
                {/*<Route path="/signup" component={Signup} />
                <Route path="/users/:username"
                  render={(props) => <Profile isAuthenticated={this.state.isAuthenticated} currentUser={this.state.currentUser} {...props}  />}>
                </Route>
                <Route path="/events/:id"
                         render={(props) => <Event isAuthenticated={this.state.isAuthenticated} currentUser={this.state.currentUser} {...props}  />}>
                </Route>*/}
                <PrivateRoute path="/dash" component={Dashboard} handleLogout={this.handleLogout} currentUser={this.state.currentUser} isAuthenticated={this.state.isAuthenticated} />

                <Route component={NotFound} />

              </Switch>
    );
  }
}

export default withRouter(App);
