import React, { Component } from 'react';

import {
  Route,
  withRouter,
  Switch,
  Redirect
} from 'react-router-dom';

import { getCurrentUser,login } from '../util/APIUtils';
import { ACCESS_TOKEN } from '../variables/constants';

import Dashboard from '../layouts/Dashboard/Dashboard.jsx';
import Login from '../views/Login';
import NotFound from '../views/NotFound';
//import LoadingIndicator from '../components/LoadingIndicator/LoadingIndicator';
import PrivateRoute from './PrivateRoute';

// Sweet alert
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";
const MySwal = withReactContent(Swal);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUser: null,
      isAuthenticated: true,//false,
      isLoading: true
    };
    this.handleLogout = this.handleLogout.bind(this);
    this.loadCurrentUser = this.loadCurrentUser.bind(this);
    this.handleLogin = this.handleLogin.bind(this);
    this.notify = this.notify.bind(this);
  }

  /*componentWillMount(){
    this.loadCurrentUser();
    alert(this.state.isAuthenticated);
  }*/


  loadCurrentUser() {
    this.setState({
      isLoading: true
    });
    getCurrentUser()
    .then(response => {
      this.setState({
        currentUser: response,
        isAuthenticated: true,
        isLoading: false
      });
      //alert(this.state.isAuthenticated);
    }).catch(error => {
      this.setState({
        isLoading: false
      });  
    });
  }

  componentDidMount() {
    this.loadCurrentUser();
  }


  handleLogin = (loginRequest) => {

    //let loginRequest=null;

    const Toast = MySwal.mixin({
      toast: true,
      position: 'top-end',
      showConfirmButton: false,
      timer: 2000
    });
    login(loginRequest)
        .then(response => {
          localStorage.setItem(ACCESS_TOKEN, response.accessToken);
          Toast.fire({
            type: 'success',
            title: 'Vous êtes connecté avec succès.'
          });
          //this.loadCurrentUser();
          this.props.history.push("/user/transfer");
        }).catch(error => {
      if(error.status === 401) {
        Toast.fire({
          type: 'error',
          title: 'Votre nom d\'utilisateur ou votre mot de passe est incorrect. Veuillez réessayer!'
        });
      } else {
        this.setState({
          isAuthenticated: true
        });
        this.props.history.push("/user/transfer");
        Toast.fire({
          type: 'warning',
          title: 'Pardon! Quelque chose s\'est mal passé. Veuillez réessayer!'
        });
      }
    });
  };



  handleLogout = ()=> {
    localStorage.removeItem(ACCESS_TOKEN);

    this.setState({
      currentUser: null,
      isAuthenticated: false
    });
    const Toast = MySwal.mixin({
      toast: true,
      position: 'top-end',
      showConfirmButton: false,
      timer: 2000
    });
    Toast.fire({
      type: 'success',
      title: 'Vous êtes déconnecté avec succès.'
    });
    this.props.history.push('/login');
  };

  render() {
    /*if(this.state.isLoading) {
      return <LoadingIndicator />
    }*/
    return (
        <>
            <Switch>
              <Route path="/login"
                     render={(props) => <Login handleLogin={this.handleLogin} {...props} />} />
              <PrivateRoute path="/user" component={Dashboard} handleLogout={this.handleLogout} history={this.props.history} currentUser={this.state.currentUser} isAuthenticated={this.state.isAuthenticated}/>
              <Redirect exact from="/" to="/login" />
              <Route component={NotFound} />
            </Switch>
        </>
    );
  }
}

export default withRouter(App);
